	public function executeUpdate() {

		if (!$this->getRequestParameter('id')) {
			$issue = new Issue();
		} else {
			$issue = IssuePeer::retrieveByPk($this->getRequestParameter('id'));
			$this->forward404Unless($issue);
		}


		// 公共部分
		$issue->setId($this->getRequestParameter('id'));
		$issue->setPriority($this->getRequestParameter('priority'));
		$issue->setTitle($this->getRequestParameter('title'));
		$issue->setDescription($this->getRequestParameter('description'));
		$issue->setSolution($this->getRequestParameter('solution'));;
		$issue->setReference($this->getRequestParameter('reference'));
		$issue->setStatus($this->getRequestParameter('status'));


		$issue->setType($this->getRequestParameter('type'));

		$typeString	= $issue->getTypeString();
		$method		= sprintf("saveEdit%s", $typeString);

		$issue->$method($this);


		$issue->handleDecision();



		// Upload 开始
		$baseId		= $issue->getId();
		$fileName	= $this->getRequest()->getFileName('file');

		$arrNames	= array();
		if (is_array($fileName)) {
			$arrNames	= $fileName;
		} else {
			$arrNames[0]	= $fileName;
		}

	#	$baseId

		$uploadDir	= sfConfig::get('sf_upload_dir') . '/' . $baseId . '/';
		if (!file_exists($uploadDir)) {
			mkdir($uploadDir);
		}

		// 获取旧的文件列表
		$arrUploadFiles	= $issue->getUploadFiles();

		foreach ($arrNames as $key => $name) {

			$name		= trim($name);
			if (0 == strlen($name)) {
				continue;
			}

			$name		= urlencode($name);
			$targetFile	= $uploadDir . $name;

			if (file_exists($targetFile)) {
			#	$targetFile	= date('Ymd') . '_' . $this->getUser()->getId() . '_' . $targetFile;
				unlink($targetFile);
			}

			if (!in_array($name, $arrUploadFiles)) {
				$arrUploadFiles[] = $name;
			}

			$this->getRequest()->moveFile(sprintf('file[%d]', $key), $targetFile);
		}
		$issue->getUploadFiles($arrUploadFiles);

		$issue->$method($this);
		// Upload 结束



	#	var_dump($fileName);
